/* Bing多栏搜索 */
body {
    background: #F2F2F2 !important;
    color: #000 !important;
}
    #b_header { /* 顶部 */
        width: auto;
        box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
    }
        .b_scopebar {
              margin-top: 0;
              border-bottom: none;
        }
        .b_op_actv {
            display: none !important;
        }
    #b_content { /* 内容 */
        padding: 20px 70px 0;
    }
        #b_results { /* 搜索结果 */
            width: auto;
            margin-right: -10px;
        }
            #b_results > li{ /* 搜索结果条目 */
                margin: 0;
                padding: 0;
                background-color: transparent;
            }
            #b_results > .b_mop , /* 必应词典 */
            #b_results > .b_algo,
            .nws_itm, .nws_itmb { /* 最新相关信息 */
                display: inline-block;
                margin-right: 10px;
                margin-bottom: 8px;
                padding: 12px;
                min-width: 590px;
                min-height: 90px;
                background: #FFF;
                box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
                box-sizing: border-box;
                vertical-align: top;
                overflow: hidden;
            }
            #b_results > .b_mop:hover , /* 必应词典 */
            #b_results > .b_algo:hover {
                box-shadow: 0 2px 16px rgba(0, 0, 0, 0.1);
            }
            @media (min-width: 740px) {
                #b_results > .b_mop,
                #b_results > .b_algo,
                .nws_itm, .nws_itmb {
                    width: calc(100% - 10px);
                }
            }
            @media (min-width: 1340px) {
                #b_results > .b_mop,
                #b_results > .b_algo,
                .nws_itm, .nws_itmb {
                    width: calc((100% - 20px)/2);
                }
            }
            @media (min-width: 1940px) {
                #b_results > .b_mop,
                #b_results > .b_algo,
                .nws_itm, .nws_itmb {
                    width: calc((100% - 30px)/3);
                }
            }
            #b_results > li:first-child { /* 第一条搜索结果条目 */
                padding-top: 12px;
            }
                .b_title,
                .b_algo>h2{ /* 搜索结果条目标题 */
                    margin: -12px -12px 6px -12px;
                    padding: 6px 12px;
                    background-color: rgb(248, 248, 248);
                }
                .dict_oa { /* 必应词典 */
                    border: none;
                    padding: 0;
                    margin: 0;
                }
            #b_results .b_ans .b_rs h2, #b_results .b_rs .b_vlist2col li {
                padding-bottom: 12px;
            }
            #b_results .b_rs .b_vlist2col li {
                display: inline-block;
                margin-right: 24px;
            }
            #b_results .b_rs .b_vlist2col li:hover {
                text-decoration: underline;
            }
        #b_context { /* 补充结果 */
            display: none;
        }
    .b_underSearchbox,
    .trans_button_group,
    #ev_talkbox_wrapper,
    .tipContainer {
        display: none;
    }

