/*====== HEAD =======*/
    /*---- 导航条 ----*/
      /*页面整体下移以防止被导航栏遮挡*/
      body{margin-top:40px !important; } 
      /* 导航条置于顶部，设固定宽度防止与搜索栏互相遮挡 */
      #comeing_nv{
        position:absolute ;
        width:100%;
        left:0;
        top:0;
        border-radius:0 !important;
        background:#33373B !important;
        width:100%;
      }
      /*隐藏导航条上的换肤按钮*/
      .comeing_cs{display:none;} 
      /* 对导航栏项目的字体、外观进行调整 */
      #comeing_nv li{
        background:#33373B !important;
        height:40px !important;
        line-height:38px !important;
        border-right:1px solid #555;
      }
      #comeing_nv li a{
        color:#C0C0C0 !important;
        height:40px !important;
        font-size:14px !important;
        font-weight:700 !important;
        text-shadow:none !important;
        background:#33373B !important;
      }
    /*-END*/
    /*---- 搜索条 ----*/
      /*搜索栏移到顶部导航栏显示，置于底层设定最小宽度，防止因窗口过小而与导航条互相遮挡*/
      #scbar{
        position:absolute;
        width:100%;
        left:0;
        top:0;
        padding:0 !important;
        height:40px !important;
        background:#33373B ;
        min-width:1195px ;
      }
      #scbar table{float:right;margin-top:5px;margin-right:8px !important;}/*搜索栏位置调整*/
      #scbar tr td{border:1px solid #4C9ED9 !important;} /*搜索栏边框*/
      #scbar .xg1,#scbar .xg1 a{color:#535353 !important;}/*搜索栏文字颜色*/
      .scbar_btn_td{
        border-right:3px solid #4C9ED9 !important;
        background: url('data:image/png;base64,') !important;
      }
      .scbar_txt_td{ background:#FFF !important;}
      .scbar_type_td{ background:#FFF !important;}
      #scbar_type{
        background:url('http://a.ikafan.com/image/common/arw_r.gif?LYD');
        background-repeat: no-repeat;
        background-position: 85% 50%;
      }
    /*-END*/
    /*---- 个人信息 ----*/
      #comeing_toptb{background:#FFF !important;}/*更改用户信息栏背景色*/
      #comeing_toptb .z{display:none ;} /*隐藏用户信息栏左边个性化操作*/
      #comeing_toptb .wp.cl{width:100%!important;}
      #comeing_toptb .y{padding-right:10px !important;} /*右边个人信息右移*/
      #comeing_toptb .y .pipe,.qq_bind{display:none;} /*隐藏QQ、微信、分隔条（也会隐藏"签到"）*/
      /*修复签到信息的显示*/
      #pper_a > img:nth-child(1),#pper_b > img:nth-child(1){
        display:inline !important;
        padding-right:3px;}
      /* 调整隐藏分隔条后个人信息间距 */
      #cuser,#pm_ntc,#myprompt{padding-left:5px;}
      #myprompt{padding-right:5px;}
    /*-END*/
    /*---- 页面路径 ----*/
      /*帖子路径移到白条上显示*/
      #pt{height:0 !important;}
      #pt .z{
        position:absolute;
        top:45px;
        left:15px;
      } 
      .logo{display:none !important;} /*隐藏卡饭LOGO*/
      /*隐藏导航栏下面多余白条*/
      .comeing_mu{display:none !important;} 
      #coeming_hd >.wp{height:0 !important;}
    /*-END*/
  /*=END*/
  /*====== COMEING_BOX =======*/
    /*边框直角美化*/
    .comeing_box .bm{border-radius:0px !important;}
    /*调整标题高度*/
    .comeing_box .bm_h{
       border-radius:0 !important;
       height:38px !important;
       line-height:38px !important;
       border-bottom:1px solid #DCDCDC !important;
    }
    /*修复调整标题后首页底部的友情链接模块*/
    .comeing_box .bm.lk .bm_h{ 
       height:10px !important;
       border-bottom:0px solid #DCDCDC !important;
    }
    /*修改标题字体*/
    .comeing_box .bm_h h2, .comeing_box .bm_h h3, .comeing_box .bm_h h4 {
       color:#666 !important;
       font-weight: 700 !important;
       font-size: 14px !important;
       text-shadow: none !important;
    }
    /*右侧收起展开按钮微微上移*/
    .comeing_box .bm_h .o img {margin-top: 10px !important;}
  /*=END*/
  /*====== PAGE & THREAD =======*/
    /*页码显示区块直角处理*/
    .pg a, .pg strong, .pgb a, .pg label {border-radius: 0px;}
    /*翻页按钮直角处理*/
    .pgbtn a{
      border-radius:0 !important;
      border:1px solid #CDCDCD !important;
    }

    /*类别显示区块直角处理*/
    #thread_types a{
      padding:3px 8px 4px !important;
      border-radius:0 !important;
    }
  /*=END*/
  /*====== #主站 =======*/
    /*主站推荐模块与分享&发现模块直角处理*/
    .comeing_show_box {
        box-shadow:none !important;
        border:1px solid #DCDCDC !important;
        border-radius: 0px !important;
    }
    #comeing_showtabs li { border-radius: 0 !important; }
    #pt .y{ display:none ; }/*隐藏主站"卡饭要干啥0.0~~~"*/
  /*=END*/

/*====== 将顶部翻页与类别显示合并为一个模块 ======*/
    /*顶部页码条外观修改*/
    #pgt{
      border:1px solid #DDD !important;
      height:38px !important;
      line-height:38px !important;
      margin-bottom:0 !important;
      border-bottom:none !important;
      padding-left:10px !important;
      padding-right:10px !important;
      background: url('http://fj.ikafan.com/template/comeing_city/comeing/box/bm_h.gif') repeat-x scroll 0px -42px transparent;
    }
    /*类别显示外观调整*/
    #thread_types{
      border:1px solid #DCDCDC !important;
      box-shadow: 0px 2px 0px 0px #DDD;
      background:#FFF !important;
      padding-left:10px;
      margin-bottom:10px !important;
    }
    /*收起按钮左移*/
    .cttp .unfold, .cttp .fold {margin-right:-25px;}
  /*=END*/

/*====== THREADVIEW =======*/
    /*顶部发帖与回复按钮位置调整*/
    #pgt #newspecialtmp,#pgt #newspecial,#pgt #post_replytmp,#pgt #post_reply{
      margin-top:2px;
    }
    /*帖子标题,正文,回复界面直角美化*/
    .comeing_vt_pf {
      border:1px solid #DCDCDC !important;
      border-bottom:none !important;
      border-radius: 0px !important;
    }
    .comeing_threadview .comeing_viewthread {
      border: 1px solid #DCDCDC;
      border-radius: 0px !important;
      box-shadow: none !important;
    }
    .plc .ts {font-size:15px !important;}/*修改帖子标题字体大小*/
    .pls .ptn { margin-top:2px !important; }/*查看与回复信息下移*/
  
    /*隐藏用户签名和部分信息*/
    .sign{display:none !important;}/*隐藏签名*/
    .pls>.pil.cl ,.pls>.tns.xg2 ,.pls>p{display:none !important;}/*隐藏部分用户信息*/
  
  /*=END*/

.comeing_index_firstarea,.comeing_index_wad,.comeing_index_fc,.comeing_index_fguide,.comeing_index_fshow, .comeing_index_map {
        border-radius: 0px !important;
        box-shadow:none !important;
      }

/*整个页面背景*/
  #nv_forum,#nv_home{
    background:#222E3E !important;
  }
  /*COMEING_BOX标题背景*/
  .comeing_box .bm_h{
    background-color: #E4E6E9 !important;;
    background-image: -moz-linear-gradient(center top , #EFF0F2 0%, #E4E6E9 100%)!important;;
  }
  /*导航条与搜索条背景*/
  #comeing_nv,  #comeing_nv li,  #comeing_nv li a,  #scbar{
    background-color: #2D3C4E !important;;
    background-image: -moz-linear-gradient(center top , #3F546D 0%, #2D3C4E 100%) !important;
  }
   #scbar tr td{border:1px solid #3635AE !important;} /*搜索栏边框*/
   .scbar_btn_td{/*搜索按钮背景*/
     background: url('data:image/png;base64,') !important;
  }

  /*导航条分隔条颜色*/
  #comeing_nv li a{color:#CCC !important;}
  #comeing_nv li{border-right:1px solid #415165;}
  /*------ 发帖&回复按钮 ------*/
    #newspecialtmp > img ,#newspecial > img ,#post_replytmp > img ,#post_reply >img ,#pgt >a >img{
    opacity:0;
    }
    #newspecialtmp,#newspecial{ /*发帖按钮*/
    border:1px solid #CDCDCD;
